mapper.update:
update set_meal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.delete:
delete from set_meal
where id in

    #{id}

mapper.select:
select count(0)
from set_meal
where category_id = #{categoryId}

select sm.*, c.name categoryName from set_meal sm join category c on sm.category_id = c.id
sm.category_id = #{categoryId} and sm.name like concat('%',#{name},'%') and sm.status = #{status}
order by sm.create_time desc

select sd.name, sd.copies, d.image, d.description
from set_meal_dish sd left join dish d on sd.dish_id = d.id
where sd.set_meal_id = #{setMealId}

select * from set_meal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
